import React from 'react'
import { Modal } from 'rsuite';
import PortfolioItem from '.';
import { useModalState } from '../../helper_functions'

const PortfolioButton = ({ icon }) => {
  const [isOpen, setIsOpen] = useState(defaultValue);

  const open = useCallback(() => {
    setIsOpen(true)
  },[])

  const close = useCallback(() => {
    setIsOpen(false)
  }, [])
  return (
    <>
      <button onClick={open} className="portfolio-frame">
      <img className="img" src={icon} />
      </button>
      {/* <PortfolioItem show={isOpen} onHide={close}/>
       */}
      <PortfolioItem show={isOpen} close={() => close()}/>
    </>
  )
}

export default PortfolioButton
