import React, { useCallback, useState } from 'react'
import { Modal } from 'rsuite';
import PortfolioItem from '.';
icon={irisIcon} 
            title="Iris Data Visualization"
            description="<p>this is p1</p> <p>this is p2</p>"
            codeURL=""
            siteURL=""

const PortfolioButton = ({ icon,title,description,codeURL,siteURL }) => {
  const [isOpen, setIsOpen] = useState(false);
  const open = useCallback(() => {
    setIsOpen(true)
  }, [])

  const close = useCallback(() => {
    setIsOpen(false)
  }, [])
  return (
    <>
      <button onClick={open} className="portfolio-frame">
        
      <img className="img" src={icon} />
      </button>
      <PortfolioItem show={isOpen} image={icon} close={() => close()}/>
    </>
  )
}

export default PortfolioButton
